import React, { useEffect, useState } from "react";
import { Chart } from "react-chartjs-2";
import { Pie, Bar } from "react-chartjs-2";
import axios from "axios";

export default function Dashboard() {
  const [summary, setSummary] = useState({
    income: 0,
    expense: 0,
    byCategory: {},
  });

  useEffect(() => {
    // fetch monthly report
    axios
      .get("/api/reports/current")
      .then((r) => setSummary(r.data))
      .catch(() => {});
  }, []);

  const pieData = {
    labels: Object.keys(summary.byCategory || {}),
    datasets: [
      {
        data: Object.values(summary.byCategory || {}),
        backgroundColor: ["#60A5FA", "#F97316", "#34D399", "#F472B6"],
      },
    ],
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-lg font-semibold mb-2">Dashboard</h2>
      <div className="flex gap-4 mb-4">
        <div className="p-4 bg-green-50 rounded flex-1">
          Income: ${summary.income}
        </div>
        <div className="p-4 bg-red-50 rounded flex-1">
          Expense: ${summary.expense}
        </div>
      </div>
      <div>
        <h3 className="mb-2">Expenses by Category</h3>
        <div style={{ maxWidth: 400 }}>
          <Pie data={pieData} />
        </div>
      </div>
    </div>
  );
}
