import React, { useEffect, useState } from "react";
import { Chart as ChartJS } from "chart.js";
import { Pie } from "react-chartjs-2";
import axios from "axios";

export default function Dashboard() {
  const [summary, setSummary] = useState({
    totalIncome: 0,
    totalExpense: 0,
    byCategory: {},
  });
  const [error, setError] = useState(null);

  // Fetch dashboard summary from backend
  const fetchSummary = () => {
    axios
      .get("/api/reports/summary")
      .then((r) => {
        setSummary(
          r.data || { totalIncome: 0, totalExpense: 0, byCategory: {} }
        );
        setError(null);
      })
      .catch((err) => {
        console.error("Error fetching reports:", err);
        setError("Failed to load report data");
      });
  };

  useEffect(() => {
    fetchSummary();
    // Listen for custom event when transaction is added/removed
    window.addEventListener("transactionUpdated", fetchSummary);
    return () => window.removeEventListener("transactionUpdated", fetchSummary);
  }, []);

  const pieData = {
    labels: Object.keys(summary.byCategory || {}),
    datasets: [
      {
        label: "Expenses by Category",
        data: Object.values(summary.byCategory || {}),
        backgroundColor: [
          "#60A5FA",
          "#F97316",
          "#34D399",
          "#F472B6",
          "#FBBF24",
        ],
        borderColor: ["#1E40AF", "#C2410C", "#059669", "#BE185D", "#92400E"],
        borderWidth: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    plugins: {
      legend: {
        position: "bottom",
      },
    },
  };

  return (
    <div className="bg-white p-6 rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Dashboard</h2>
      {error && <div className="text-red-600 mb-4">{error}</div>}
      <div className="grid grid-cols-2 gap-4 mb-6">
        <div className="p-4 bg-green-50 rounded">
          <p className="text-sm text-gray-600">Total Income</p>
          <p className="text-2xl font-bold text-green-600">
            ${(summary.totalIncome || 0).toFixed(2)}
          </p>
        </div>
        <div className="p-4 bg-red-50 rounded">
          <p className="text-sm text-gray-600">Total Expense</p>
          <p className="text-2xl font-bold text-red-600">
            ${(summary.totalExpense || 0).toFixed(2)}
          </p>
        </div>
      </div>
      <div>
        <h3 className="text-sm font-semibold mb-3">Expenses by Category</h3>
        {Object.keys(summary.byCategory || {}).length > 0 ? (
          <div style={{ maxWidth: 350, margin: "0 auto" }}>
            <Pie data={pieData} options={chartOptions} />
          </div>
        ) : (
          <p className="text-gray-400 text-center py-6">No expense data yet</p>
        )}
      </div>
    </div>
  );
}
